import React, { useState, useEffect } from 'react'
import { Text, View, StyleSheet, TextInput, TouchableOpacity, Touchable } from 'react-native'
import { Formik } from 'formik'
import * as yup from 'yup'

const loginValidationSchema = yup.object().shape({
  name: yup
    .string()
    .required('é necessário um nome valido'),
  email: yup
    .string()
    .email('O email nao é válido')
    .required('é necessário um email válido'),
  password: yup
    .string()
    .min(6, ({min}) => `A senha deve conter pelo menos  ${min} caractres`)
    .required('é necessário uma senha válida'),
})


export default function App({navigation}) {
    const [cadastrou, setCadastrou] = useState(false);
    const [mensagem, setMensagem] = useState("");

  function createUserInNodeApi(values) {
        var myHeaders = new Headers()
        myHeaders.append("Content-type", "application/json")

        var raw = JSON.stringify({
          "name": values.name,
          "email": values.email,
          "password": values.password
        })

        var requestOption = {
          method: 'POST',
          headers: myHeaders,
          body: raw,
          redirect: 'follow',
          mode: 'cors'
        }

        fetch("http://localhost:8080/addUser", requestOption)
          .then((response) => { return response.json() })
          .then((res) => {
               if (res.error) {
                   setCadastrou(false);
               } else {
                   setCadastrou(true);
               }
               setMensagem(res.message);
           })
          .catch((error) => { console.log(error); })

    }

    return (
      <View style={styles.container}>
        <Text style={styles.subTitle}>Cadastro de usuário</Text>
        <Formik
          style={{}}
          validateOnMount={true}
          validationSchema={loginValidationSchema}
          initialValues={
            {
              name: "",
              email: "",
              password: "",
            }
          }
          onSubmit={values => createUserInNodeApi(values)}
        >
        {
          ({ handleChange, handleBlur, errors, values, touched, handleSubmit }) => (
            <>
              <TextInput
                name="name"
                type={"name"}
                value={values.name}
                onChangeText={handleChange("name")}
                onBlur={handleBlur("name")}
                style={styles.inputStyle}
                placeholder="Digite seu nome"
              />
              {(errors.name && touched.name) &&
                <Text style={styles.titleErros}>{errors.name}</Text>
              }

              <TextInput
                name="email"
                type={"email"}
                value={values.email}
                onChangeText={handleChange("email")}
                onBlur={handleBlur("email")}
                style={styles.inputStyle}
                placeholder="Digite seu email"
              />
              {(errors.email && touched.email) &&
                <Text style={styles.titleErros}>{errors.email}</Text>
              }

              <TextInput
                name="password"
                type={"password"}
                value={values.password}
                onChangeText={handleChange("password")}
                onBlur={handleBlur("password")}
                style={styles.inputStyle}
                placeholder="Digite sua senha"
                secureTextEntry
              />
              {(errors.password && touched.password) &&
                <Text style={styles.titleErros}>{errors.password}</Text>
              }

              {
                  mensagem != "" ? 
                    !cadastrou ? (
                        <Text style={styles.titleErros}>Erro ao cadastrar usuário!</Text>
                    ) : (
                        <Text style={styles.titleSucesso}>Usuário cadastrado com sucesso!</Text>
                    ) 
                    :
                    ""
              }

              <TouchableOpacity style={styles.button} onPress={handleSubmit}>
                <Text style={styles.buttonTitle}>Cadastrar</Text>
              </TouchableOpacity>
            </>
          )
        }

      </Formik>
    </View>
  )
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    justifyContent: 'flex-start',
    alignItems: 'center'
  },
  title: {
    fontSize: 28,
    color: 'gray',
    fontWeight: '700',
    marginTop: '10%'
  },
  subTitle: {
    fontSize: 16,
    color: 'gray',
    fontWeight: '700',
    marginTop: '2%'
  },
  inputStyle: {
    width: 200,
    height: 35,
    //backgroundColor:'#0f1b59',
    borderRadius: 10,
    marginTop: 30,
    padding: 10,
    borderColor: '#0f1b59',
    borderWidth: 1
  },
  button: {
    width: 200,
    height: 35,
    backgroundColor: '#0f1b59',
    borderRadius: 10,
    marginTop: '8%',
  },
  buttonTitle: {
    fontSize: 16,
    color: '#f5f5f5',
    fontWeight: '700',
    textAlign: 'center',
    marginTop: 8
  },
  titleErros: {
    fontSize: 12,
    marginTop: 8,
    color: 'red',
    fontWeight: "600"
  },
  titleErros: {
    fontSize: 12,
    marginTop: 8,
    color: 'green',
    fontWeight: "600"
  }
})


